@article{90NeVoWi,
abstract = {The infrared Fourier-transform spectrum of gaseous H2CNN (diazomethane) has been analysed between 800 and 250 wavenumber, at a spectral resolution 0.07 wavenumber. A preliminary analysis of the coarse and fine rotational structure of the three vibrational-rotational bands: $\nu$5, $\nu$6 and $\nu$9 is reported. Using re structure rotational constant values, the vibration-rotation constants for the vibrational ground state are obtained as $\alpha$(A)=+4018 MHz, $\alpha$(B)= _34.78 MHz and $\alpha$(C)=_15.23 MHz. A preliminary estimate for the Coriolis zeta coefficient (a-type) is |zeta(5,9)|= 0.91 and |zeta(6,9)| = 0.13.},
author = {Nemes, L. and Vogt, J. and Winnewisser, M.},
journal = {Journal of Molecular Structure},
mendeley-groups = {FREQ_refs},
pages = {219--224},
title = {{Analysis of the three lowest frequency vibration-rotation bands of diazomethane}},
volume = {218},
year = {1990}
}

