@article{98OrMoKl,
abstract = {The far-infrared spectrum of nitryl chloride was studied using high-resolution Fourier-transform spectroscopy in the 300Ð525 cm01 region. Vibrational band centers offundamental, hot, and difference bands were determined. Furthermore, rotational and centrifugal distortion constants up to fourth order for the n3 bands of 35 ClNO2 and 37 ClNO2 (centered at 370 and 364 cm01 , respectively) were obtained. The n5 fundamental of ClNO2 (predicted around 410 cm01 ) is very weak and overlapped by difference bands.},
author = {Orphal, J and Morillon-Chapey, M. and Klee, S. and Mellau, G. Chr. and Winnewisser, M},
journal = {Journal of Molecular Spectroscopy},
mendeley-groups = {Refs_frequencies,FREQ_refs},
pages = {101--106},
title = {{The Far-Infrared Spectrum of ClNO 2 Studied by High-Resolution Fourier-Transform Spectroscopy}},
volume = {190},
year = {1998}
}

