@article{67CoIsLo,
abstract = {The parallel band due to the fundamental frequency Ye at 1076.5 cm-1 and the perpendicular band of frequency Y* at 451.3 cm-1 in the spectrum of cyano- gen fluoride have been resolved and analyzed. The ground-state rotational constant &I was measured as 0.3525 f 0.0004 cm-r, in agreement with micro- wave results and earlier infrared work on Y, . Other rotational constants measured were B' for each band and D' + DÓ for r1 . Vibrational assignments are suggested for several upper-stage bands superimposed on $\sim$1 and Ye and for certain weak bands believed to be combinations of the fundamentals.},
author = {Cole, A. R. H. and Isaacson, L. and Lord, R. C.},
journal = {Journal of Molecular Spectroscopy},
mendeley-groups = {PhD/5_HarmBenchmark,PhD/5_HarmBenchmark/Freqs_refs},
pages = {86--93},
title = {{Two Vibration-Rotation Bands of Cyanogen Fluoride}},
volume = {23},
year = {1967}
}

